// index.min.js

async function toggleBoton(){
    let navUl = document.querySelector('.nav ul');
    let nav = document.querySelector('.nav');
    let clases = navUl.classList;

    if (clases.contains('transform-100')){
        nav.classList.toggle('overflow-hidden');
        navUl.classList.toggle('transform-100');
    } else {
        navUl.classList.toggle('transform-100');
        await sleep(1100);
        nav.classList.toggle('overflow-hidden');
    }
}

function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }